mapper.select:
    SELECT h.id,type_id AS typeId,title,description,price,pubdate,floorage,contact,
h.street_id AS streetId,path,district_id AS districtId  FROM House AS h LEFT JOIN `street` AS  s ON
h.street_id=s.`ID`  WHERE  h.id=#{id}

SELECT house.id, type_id,title,price,pubdate,floorage,contact,street_id,path, type.name AS tname, street.name AS sname, district.name AS dname FROM house LEFT JOIN TYPE ON house.`TYPE_ID`=type.`ID` LEFT JOIN street ON house.`street_id`=street.`ID` LEFT JOIN district ON street.`district_id`=district.`ID` WHERE ispass=1 AND isdel=0
and title like concat('%',#{title},'%')
and district_id =#{did}
and street_id =#{sid}
and type_id =#{tid}
and price >=#{startPrice}
and price <=#{endPrice}
